const charRegex = require('char-regex');

const stringLength = string => {
	if (string === '') {
		return 0;
	}

	const strippedString = stripAnsi(string);

	if (strippedString === '') {
		return 0;
	}

	return strippedString.match(charRegex()).length;
};

module.exports = stringLength;
